package day06_operators2_Scanner;

public class OperatorsPrecedence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hello " + 6 + 10); // onde olani once agiliyor
		
		System.out.println(6+ 10 +"Hello "); // burada onde matematik islemlerini once algiliyor
		
		System.out.println("Hello " + (6+ 10) );
		

	}

}
